import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { setAlert } from '../../redux/actions/messages';
import { logoutCurrentUser } from '../../redux/actions/session';

export const Page = ({ setAlert, logoutCurrentUser, children }) => {
    useEffect(() => {
        //get companies
    });
    const logout = async () => {
        try {
            await axios.delete('/api/user');
            logoutCurrentUser();
            setAlert(
                'You have been logged out successfully.',
                'success',
                'login page',
                'false'
            );
        } catch (err) {
            console.log('logout err', err);
        }
    };
    return (
        <div className='page'>
            <header>
                <Link
                    to='/dashboard'
                    onMouseDown={(e) => e.preventDefault()}
                    className='main-nav__logo'
                    onClick={() => {
                        setAlert(
                            `Profile's settings have been now desplayed on the page `,
                            'success'
                        );
                    }}
                >
                    invoice
                </Link>
                <Link
                    to='/dashboard/profile'
                    className='main-nav__link'
                    onMouseDown={(e) => e.preventDefault()}
                    onClick={logout}
                >
                    Sign Out
                </Link>
            </header>
            <main>{children}</main>
        </div>
    );
};

Page.propTypes = {};

const mapStateToProps = (state) => ({});

const mapDispatchToProps = {
    setAlert,
    logoutCurrentUser,
};

export default connect(mapStateToProps, mapDispatchToProps)(Page);
