import { LOGIN_USER, LOGOUT_USER } from '../actions/types';

const initialState = {
    user: null,
    authenticated: false,
};

export default function (state = initialState, { type, user }) {
    switch (type) {
        case LOGIN_USER:
            localStorage.setItem('persist', 'auth');
            return {
                ...state,
                user,
                authenticated: true,
            };
        case LOGOUT_USER:
            localStorage.removeItem('persist');
            return {
                ...state,
                user: null,
                authenticated: false,
            };
        default:
            return state;
    }
}
