import axios from 'axios';
import React from 'react';
import { Redirect } from 'react-router-dom';
import { dialogBox } from '../../../components/alerts/alertsFuns';

export const alertUnsavedChanges = async (
    profile,
    getProfile,
    setProfileTab,
    history,
    setAlert
) => {
    try {
        const profileDB = await axios.get('/api/profiles');

        if (
            JSON.stringify(profile.details) !==
            JSON.stringify(profileDB.data.details)
        ) {
            const msg = `You have some unsaved changes in your profile form.`;
            const cancelBtnText = 'Leave without saving';
            const confirmBtnText = 'Return to form!';
            const cancelCb = () => {
                getProfile();
            };
            const confirmCb = () => {
                history.push('/dashboard/profile');
                setProfileTab('form');
            };
            dialogBox({
                msg,
                cancelBtnText,
                confirmBtnText,
                cancelCb,
                confirmCb,
            });
        }
    } catch (err) {
        setAlert(
            `We think you may have some unsaved changes in profile form. Unfortunately due to server error we weren't able to proceed with them.`,
            'danger',
            false,
            15000
        );
        console.log('@@@ Unsaved Changes - Profile @@@', err);
    }
};
